.sidebar {
  width: 320px;
  height: 100vh;
  background-color: $color-main-light;
}

.sidebar__top {
  display: flex;
  align-items: center;
  padding: 16px 12px;
}

.sidebar__burger {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
  padding: 0;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: none;
  cursor: pointer;
  transition: background-color .3s;

  &:hover {
    background-color: $gray-lightest;
    transition: background-color .3s;
  }
}

.sidebar__burger-wrapper {
  position: relative;
  display: flex;
  align-items: center;
  padding: 0;
  width: 28px;
  height: 20px;

  &:before,
  &:after {
    content: '';
    position: absolute;
    width: 100%;
    height: 2px;
    background-color: $gray-darkest;
  }

  &:before {
    bottom: 0;
  }

  &:after {
    top: 0;
  }

  div {
    width: 100%;
    height: 2px;
    background-color: $gray-darkest;
  }
}

.sidebar__search-form {
  flex-grow: 1;
}

.sidebar__search {
  input {
    padding: 12px;
    border-radius: 15px;
    border: 1px solid $white;
    box-shadow: 0px 4px 20px rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(8px);
    outline: none;
  }
}

